DEFAULT_RATIONALE = "This option does not accurately reflect the concept described in the question."

def make_question(question, correct, incorrects, difficulty, explanation=None):
    answer_options = []
    correct_text, correct_rationale = correct
    answer_options.append({
        "text": correct_text,
        "isCorrect": True,
        "rationale": correct_rationale,
    })
    for item in incorrects:
        if isinstance(item, tuple):
            text, rationale = item
        else:
            text, rationale = item, None
        answer_options.append({
            "text": text,
            "isCorrect": False,
            "rationale": rationale or DEFAULT_RATIONALE,
        })
    q = {
        "type": "multipleChoice",
        "difficulty": difficulty,
        "question": question,
        "answerOptions": answer_options,
    }
    if explanation:
        q["explanation"] = explanation
    return q


def make_term_alignment_questions(items, prompt_template=None, rationale_template=None):
    """Create multiple-choice questions that match terms to definitions.

    items: list of dicts with keys term, definition, difficulty, optional explanation.
    prompt_template: optional str with {definition} placeholder.
    rationale_template: optional str with {term} and {definition} placeholders.
    """

    if prompt_template is None:
        prompt_template = "Which term best matches the following description: {definition}?"
    if rationale_template is None:
        rationale_template = "Correct. {term} is defined as {definition}."

    if len(items) < 4:
        raise ValueError("At least four items are required to build matching questions.")

    questions = []
    total = len(items)
    for idx, item in enumerate(items):
        term = item["term"]
        definition = item["definition"]
        difficulty = item.get("difficulty", "medium")
        explanation = item.get("explanation")
        correct = (
            term,
            rationale_template.format(term=term, definition=definition),
        )
        incorrect_terms = []
        for step in range(1, 4):
            candidate = items[(idx + step) % total]["term"]
            incorrect_terms.append(candidate)
        question_text = prompt_template.format(definition=definition)
        questions.append(
            make_question(
                question_text,
                correct,
                incorrect_terms,
                difficulty,
                explanation=explanation,
            )
        )
    return questions

